import re

from django.core import validators
from django.utils.deconstruct import deconstructible
from django.utils.translation import gettext_lazy as _


@deconstructible
class ASCIIUsernameValidator(validators.RegexValidator):
    regex = r'^[\w.@+-]+\Z'
    message = _(
        'Escriba el usuario correctamente.'
    )
    flags = re.ASCII


@deconstructible
class UnicodeUsernameValidator(validators.RegexValidator):
    regex = r'^[\w.@+-]+\Z'
    message = _(
        'Escriba el nombre correcto, solo puede contener letras, '
        'números, y @/./+/-/_ .'
    )
    flags = 0


deconstructible
class UnicodeFirst_nameValidator(validators.RegexValidator):
    regex = r'^[A-Z a-záéí ÁÉÍÓ ]+\Z'
    message = _(
        'Escriba el nombre correcto, solo puede contener letras '
    )
    flags = 0
    
    
@deconstructible
class UnicodeLast_nameValidator(validators.RegexValidator):
    regex = r'^[A-Z a-záéí ÁÉÍÓ ]+\Z'
    message = _(
        'Escriba los apellidos correctamente, solo pueden contener letras '
    )
    flags = 0